DROP TABLE IF EXISTS `bai_viet`;
CREATE TABLE `bai_viet` (
  `id` int NOT NULL AUTO_INCREMENT,
  `tieu_de` varchar(255) DEFAULT NULL,
  `hinh_anh` varchar(255) DEFAULT NULL,
  `noi_dung` longtext,
  `ngay` datetime DEFAULT NULL,
  `nguoi_viet_id` int DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `bai_viet_ibfk_1` (`nguoi_viet_id`),
  CONSTRAINT `bai_viet_ibfk_1` FOREIGN KEY (`nguoi_viet_id`) REFERENCES `nguoi_dung` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=45 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

DROP TABLE IF EXISTS `binh_luan`;
CREATE TABLE `binh_luan` (
  `id` int NOT NULL AUTO_INCREMENT,
  `ma_bai_viet` int DEFAULT NULL,
  `ma_nguoi_dung` int DEFAULT NULL,
  `ngay_binh_luan` datetime DEFAULT NULL,
  `noi_dung` varchar(255) DEFAULT NULL,
  `id_cha` int DEFAULT NULL,
  `tag_user_name` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `binh_luan_ibfk_1` (`ma_bai_viet`),
  KEY `binh_luan_ibfk_2` (`ma_nguoi_dung`),
  KEY `binh_luan_ibfk_3` (`id_cha`),
  CONSTRAINT `binh_luan_ibfk_1` FOREIGN KEY (`ma_bai_viet`) REFERENCES `bai_viet` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `binh_luan_ibfk_2` FOREIGN KEY (`ma_nguoi_dung`) REFERENCES `nguoi_dung` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `binh_luan_ibfk_3` FOREIGN KEY (`id_cha`) REFERENCES `binh_luan` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=73 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

DROP TABLE IF EXISTS `luu`;
CREATE TABLE `luu` (
  `id` int NOT NULL AUTO_INCREMENT,
  `ma_bai_viet` int DEFAULT NULL,
  `ma_nguoi_dung` int DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `luu_ibfk_1` (`ma_bai_viet`),
  KEY `luu_ibfk_2` (`ma_nguoi_dung`),
  CONSTRAINT `luu_ibfk_1` FOREIGN KEY (`ma_bai_viet`) REFERENCES `bai_viet` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `luu_ibfk_2` FOREIGN KEY (`ma_nguoi_dung`) REFERENCES `nguoi_dung` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=95 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

DROP TABLE IF EXISTS `nguoi_dung`;
CREATE TABLE `nguoi_dung` (
  `id` int NOT NULL AUTO_INCREMENT,
  `nick_name` varchar(255) DEFAULT NULL,
  `email` varchar(255) DEFAULT NULL,
  `pass_word` varchar(255) DEFAULT NULL,
  `avatar` varchar(255) DEFAULT NULL,
  `quyen` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

DROP TABLE IF EXISTS `thich`;
CREATE TABLE `thich` (
  `id` int NOT NULL AUTO_INCREMENT,
  `ma_bai_viet` int DEFAULT NULL,
  `ma_nguoi_dung` int DEFAULT NULL,
  `ngay_thich` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `thich_ibfk_1` (`ma_bai_viet`),
  KEY `thich_ibfk_2` (`ma_nguoi_dung`),
  CONSTRAINT `thich_ibfk_1` FOREIGN KEY (`ma_bai_viet`) REFERENCES `bai_viet` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `thich_ibfk_2` FOREIGN KEY (`ma_nguoi_dung`) REFERENCES `nguoi_dung` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=150 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

INSERT INTO `bai_viet` (`id`, `tieu_de`, `hinh_anh`, `noi_dung`, `ngay`, `nguoi_viet_id`) VALUES
(28, 'Rối loạn nhân cách ranh giới (Borderline Personality Disorder)', './public/img/1683773945848-2338eee0ef9611edb1d3f921b2926cb6.png', '<h2><strong>I. Rối loạn nhân cách ranh giới (BPD) là gì ?</strong></h2><p>BPD là một loại rối loạn nhân cách về cảm xúc và hành vi mà ở đó, chủ thể có sự cực đoan về cảm xúc, thường xuyên sợ hãi với những nỗi sợ của sự cô đơn, phản bội,…chủ yếu trong các mối quan hệ người-người.</p><p>Lý do của cái tên Rối loạn nhân cách ranh giới là vì bệnh nhân thường xuyên đứng ở các “ranh giới trắng đen” trong hành vi và có xu hướng thay đổi nó rất nhanh. Ngoài ra BPD cũng rất hay bị nhầm lẫn vì các dấu hiệu khá tương đồng với các rối loạn khác (Rối loạn lưỡng cực (BD), rối loạn lo âu (AD), Rối loạn nhân cách ái kỷ (NPD)…) đòi hỏi người chuẩn đoán phải có trình độ mới có thể phân biệt rạch ròi các loại rối loạn khác với BPD.</p><figure class=\"image\"><img src=\"http://localhost:8080/./public/img/1683773779468-2338eee0ef9611edb1d3f921b2926cb6.png\"></figure><h2><strong>II. Dấu hiệu và triệu chứng</strong></h2><h3><strong>1. Dấu hiệu</strong></h3><ul><li>Sự căm ghét và tự hủy hoại bản thân: một người dễ bị BPD khi lòng tự trọng của họ xuống thấp, điều này tạo ra những xung đột nội tâm như tự phê phán, tự làm tổn thương tinh thần và thể chất chính mình.</li><li>Khó khăn trong việc kiểm soát cảm xúc: Nếu người bình thường có thể dẫn cảm xúc đi theo trình tự thì người có rối loạn về cảm xúc thường hay biến chuyển cảm xúc của họ không có thứ tự, biến chuyển ngay lập tức mọi thứ diễn ra như thể nó là ngẫu nhiên nhưng thực tế người mắc bệnh vẫn đang phải đấu tranh để kiểm soát được cảm xúc của mình trong mọi tình huống thường ngày. Việc này thường không quá gây chú ý vì người bệnh thường sẽ “nội tâm hóa” các hành vi thất thường này. Một vài trường hợp có thể mắc phải nhiều chứng bệnh rối loạn khác cùng lúc với BPD như rối loạn lưỡng cực (BD).</li><li>“Sự tấn công” tỏa ra bên ngoài (hoặc cả vào bên trong): Người bị BPD có những hành động tấn công vào tinh thần lẫn thể chất ra bên ngoài như việc chửi rủa hay gây hấn bằng hành động với người khác. Tuy nhiên những trường hợp không thể giải phóng (Hoặc họ có xu hướng nội tâm hóa), người mắc phải sẽ có hành vi hướng sự tức giận đó vào chính mình dưới dạng sự tự căm phẫn hay kìm nén để tạo ra một “quả bom nổ chậm” trong cảm xúc. Sự tức giận đó đến từ những sự thất vọng lặp đi lặp lại hoặc những yếu tố gây căng thẳng khác nhau.</li><li>Nỗi sợ trong việc bị bỏ rơi (sợ bị từ chối): Điều một người có dấu hiệu BPD rõ ràng chính là khi họ sợ bị bỏ rơi. Sự từ chối hay rời đi là một điều tồi tệ với bất kỳ ai nhưng với những người có dấu hiệu rối loạn, họ cảm thấy đây là một dấu hiệu rất tệ bị khuếch đại hóa: Sự lo âu về việc họ sẽ không trở lại, họ sẽ bỏ mình mà đi… Điều này càng làm mọi thứ tệ đi thay vì sẽ tốt hơn như mong đợi của người mắc rối loạn, ngoài ra nó còn dẫn đến dấu hiệu tiếp theo.</li><li>Sự bất ổn trong các mối quan hệ: Người mắc BPD thường có những sự bất ổn định khi đi vào các mối quan hệ từ bạn bè, người thân, hay cả người yêu,… Họ thường có xu hướng tưởng tượng, lý tưởng hóa các mối quan hệ trước và sau đó khả năng cao sẽ thất vọng, đỗ vỡ từ trong tâm hồn, sinh ra các hành động bốc đồng và vô tình phá vỡ mối quan hệ.</li><li>Hành động bốc đồng: Đây thường là dấu hiệu của các rối loạn khác như Rối loạn lưỡng cực nhưng dấu hiệu của một sự tự chủ cảm xúc kém từ người bị BPD cũng là một nguyên nhân dẫn đến các hành động như dùng chất kích thích, lạm dụng tình dục, ăn uống vô độ….</li><li>Sự phân ly trong tâm trí: Những người mắc rối loạn thường xuyên có những khoảnh khắc để tâm trí của mình mất kết nối với những gì mình đang làm, họ có thể làm việc nhưng vô cảm như thể tâm hồn họ vừa “bay” ra khỏi cơ thể.</li></ul><h3><strong>2. Triệu chứng:</strong></h3><p>Với những nguyên nhân trên, khá rõ ràng khi BPD đã ăn sâu và mạnh vào người bệnh, nó sẽ có thể khiến chủ thể làm những điều sau</p><ul><li>Tự hành hạ bản thân, phá hoại thể chất và có hành vi muốn tự sát.</li><li>Cảm xúc thay đổi thất thường, khó khăn trong việc giành lấy quyền kiểm soát cảm xúc và có thêm nhiều nỗi sợ (sợ bỏ rơi, sợ trống rỗng, sợ bị phản bội…)</li><li>Lạm dụng mạnh mẽ các chất kích thích, tạo ra nhiều hành vi bốc đồng, thiếu suy nghĩ hơn</li><li>Luôn cố gắng níu giữ các mối quan hệ một cách “mù quáng”</li><li>Thay đổi quan điểm và thái độ nhanh chóng</li></ul><h2><strong>III. Nguyên nhân:</strong></h2><p>Có nhiều nguyên nhân của Rối loạn nhân cách ranh giới nhưng điển hình có thể chia làm 2 dạng là sinh học di truyền và quá trình phát triển của chủ thể.</p><ul><li>Vấn đề sinh học của chủ thể có thể tạo ra BPD bẩm sinh từ nhỏ như việc di truyền từ bố mẹ mắc các rối loạn, hoặc các tổn thương, vấn đề về não có thể gây ra các sự khó khăn trong kiểm soát cảm xúc và hành vi dẫn đến BPD và nhiều rối loạn khác.</li><li>Vấn đề phát triển là một nguyên nhân điển hình dẫn đến các hội chứng rối loạn và không ngoại trừ BPD. Các nhà nghiên cứu tại đại học Ruhr-University Bochum ở Đức sau khảo sát đã kết luận “80% người được chẩn đoán mắc BPD có tiền sử về lạm dụng tình dục, sự bỏ rơi vào thời thơ ấu và các vấn đề liên quan”</li></ul><h2><strong>IV. Chúng ta cần làm gì để giúp đỡ những người bị BPD</strong></h2><p>Những người đã và đang bị BPD thường sẽ đi theo chiều hướng càng ngày càng tệ vì vấn đề trước luôn là “mồi lửa” để vấn đề sau bùng chảy, sự bỏ rơi rất dễ dẫn đến sự bốc đồng trong suy nghĩ chính là nguồn cơn của những mối quan hệ tiếp theo bị đổ vỡ và rời đi tiếp tục như thế theo một vòng tròn không lối thoát. Vậy nên cách tốt nhất mà một người bị BPD có thể làm chính là tìm những chuyên gia tâm lý sử dụng những liệu pháp khoa học để tìm lại chính mình.</p><p>Thế nhưng chúng ta (những người có thể đang may mắn khi chưa bị BPD) sẽ cần làm gì để cùng giúp những con người tội nghiệp đó thoát khỏi sự suy sụp trong quá trình hồi phục?</p><h3><strong>1. Chấp nhận hoặc hiểu cho những góc nhìn thật “quái dị” của họ:</strong></h3><p>BPD khiến chủ thể phản ứng một cách mất kiểm soát với rất nhiều vấn đề, vậy nên việc cố gắng chứng minh bản thân đúng hay tìm cách để đôi co với những bệnh nhân BPD chỉ khiến chúng tệ đi.</p><p>Thay vào đó, hãy chấp nhận hoặc thông cảm cho những gì họ đang làm với mình, chịu thiệt hơn bình thường một chút để tạo nên sự hòa hợp với họ. Sự thông cảm và thấu hiểu dành cho những người có khi còn chẳng hiểu nổi mình chính là động lực lớn nhất để họ cố gắng tự đưa mình ra khỏi sự tuyệt vọng thay vì cứ chì chiết và cứ bảo rằng họ đang làm những điều ngu ngốc.</p><h3><strong>2. Tạo ra “ranh giới” với những người mắc BPD:</strong></h3><p>Ngoài việc hòa hợp và cố gắng hòa chung với những người bị BPD, ta cũng phải đặt ra những ranh giới để khiến họ không bị càng ngày càng lún sâu vào cái hố của sự mất kiểm soát đó ta có thể:</p><ul><li>Tập giải thích cho họ về những sự thật trong các mối quan hệ không lý tưởng như họ luôn nghĩ.</li><li>An ủi về những điều họ đang tự dằn vặt về bản thân và cố gắng giúp họ hiểu ra giá trị cá nhân.</li><li>Tạo ra lập trường của riêng mình nhưng mềm mại trong cách chia sẻ; không cứng rắn, mạnh mẽ hay vồ vập, ta có thể chọn cách tiếp cận từ từ chậm rãi để đưa ra quan điểm cá nhân và không “làm vỡ” tâm hồn mong manh của họ.&nbsp; &nbsp; &nbsp;</li></ul><p>Tóm lại, Rối loạn nhân cách ranh giới là một rối loạn cảm xúc nguy hiểm, một khi đã phát sinh thì rất dễ mang đến những chuyển biến xấu và những hành động gây hại đến bản thân và người khác. Những hành động của những người này thường làm cho mọi thứ tệ đi theo thời gian và khiến họ càng ngày càng chìm vào hố sâu của sự tuyệt vọng. Như câu nói của người Nigeria “Cần cả một ngôi làng để nuôi lớn một đứa trẻ”, để nuôi dưỡng lại tâm hồn và sức sống bên trong người bị mắc BPD, cần sự giúp sức từ khoa học của giới chuyên gia, từ sự thấu hiểu của những người bạn, người thân của bệnh nhân để có thể giúp họ thoát ra khỏi nơi tăm tối của căn bệnh tâm lý này.</p>', '2023-05-11 02:59:05', 4);
INSERT INTO `bai_viet` (`id`, `tieu_de`, `hinh_anh`, `noi_dung`, `ngay`, `nguoi_viet_id`) VALUES
(29, 'Đằng sau bức màn đen tối của nỗi đau là kiệt tác vĩ đại của tình yêu thương', './public/img/1683851467787-9658cd90efd811edb421df1cb164742c.jpeg', '<p>Câu chuyện đằng sau thảm họa Titanic</p><p>1.514 người đã thiệt mạng trong vụ đắm tàu kinh hoàng năm ấy. Nỗi đau đã khép lại hơn một trăm năm và ngày nay, những gì người ta nghĩ tới khi nhắc đến từ \"Titanic\" có thể làm thảm họa hàng hải nghiêm trọng nhất mọi thời đại, mối tình lãng mạn của Jack và Rose hay bản tình ca bất hủ <i>My heart will go on</i>. Tuy nhiên, hầu như không ai nhận ra đằng sau bức màn đen tối của những nỗi đau và mất mát ấy là kiệt tác vĩ đại của tình yêu thương.</p><p>Charles Lightoller, thuyền phó II của tàu Titanic, khi ấy ba mươi tám tuổi, là người cuối cùng được kéo lên thuyền cứu hộ và cũng là người sống sót có chức vị cao nhất trên thuyền lúc đó. Trở về từ cõi chết và sau rất nhiều năm giấu kín và im lặng, cuối cùng ông quyết định biết 17 trang hồi ức kể lại chi tiết vụ tai nạn kinh hoàng mà ông chứng kiến. Từng câu từng chữ của ông chưa bao giờ sống động và dồn dập đến vậy. \"Chỉ cần còn sống, tôi sẽ không bao giờ quên được cảnh tượng đêm đó\", ông viết.</p><p><i>\"Phụ nữ và trẻ em lên trước!\"</i></p><p>Khi mệnh lệnh vừa vang lên, nhiều người rời thuyền cứu hộ, lặng lẽ bước ra phía sau châm điều thuốc và hút. Charles không thấy bất kỳ người phụ nữ hay trẻ em nào có ý định bỏ lại những người đàn ông thân yêu của mình. Dường như tất cả mọi người đều rất bình tĩnh trước cái chết, dù họ là thương nhân nổi tiếng hay người hầu vô danh.</p><p>Khi chiếc thuyền cứu hộ đầu tiên được đưa xuống nước, Charles hỏi một người phụ nữ họ Straw đang đứng trên boong tàu, \"Bà có muốn tôi đưa bà lên thuyền cứu hộ không?\".</p><p>Người phụ nữ lắc đầu, \"Không, tôi nghĩ mình nên ở lại tàu thì hơn\". Người chồng nghe vậy liền hỏi, \"Tại sao em không muốn lên thuyền cứu hộ?\". Bà chỉ mỉm cười trả lời, \"Vì em muốn ở cạnh anh mãi mãi\". Và Charles không bao giờ còn gặp lại đôi vợ chồng đó nữa.</p><p>Astor Đệ tứ (John Jacob Astor IV) là doanh nhân, nhà phát minh, nhà văn nổi tiếng và là một trong những người giàu nhất thế giới thời bấy giờ. Sau khi đưa người vợ mang thai năm tháng lên thuyền cứu hộ, ông châm điếu xì gà rồi hét to về phía chiếc thuyền cứu hộ đang trôi dần về nơi xa, \"Anh yêu hai mẹ con\".</p><p>Thuyền phó I ra mệnh lệnh cho Astor Đệ tứ lên thuyền, nhưng ông kiên quyết từ chối và nhường chỗ của mình cho một người phụ nữ ở khoang hạng ba. Vài ngày sau, khi bình minh ló rạng trên Đại Tây Dương, đội cứu hộ tìm thấy thi thể của ông trong tình trạng chấn thương đầu nghiêm trọng do bị va đập vào ống khói. Khối tài sản của ông đủ để chế tạo 10 con tàu Titanic, nhưng Astor Đệ tứ đã từ bỏ tất cả. Ông chọn cái chết để bảo vệ người vợ thân yêu, bảo vệ \"phụ nữ và trẻ em\" và bảo vệ nhân cách của mình.</p><p>Ben Guggenheim là tỷ phú và là nhân vật nổi tiếng trong ngành ngân hàng. Trong giờ phút nguy nan nhất, khi tất cả mọi người đều hoảng loạn và vội vã, ông thản nhiên thay bộ vest trang trọng nhất và tuyên bố, \"Tôi phải chết trịnh trọng như một quý ông\". Trong lời nhắn gửi cho vợ, ông viết, \"Trên con tàu này, không có bất kỳ phụ nữ nào vì bị anh cướp chỗ trên thuyền cứu hộ mà bị bỏ lại trên boong tàu. Nếu có chết, anh cũng sẽ chết như một người đàn ông đích thực\".</p><figure class=\"image\"><img src=\"http://localhost:8080/./public/img/1683851428226-9658cd90efd811edb421df1cb164742c.jpeg\"><figcaption>Đây là bìa cuốn sách mình trích dẫn bài viết - chủ yếu vì thấy con voi che ô cho con chó và ngồi cùng nhau đáng yêu quá nên mới viết bài&nbsp;</figcaption></figure><p>Một thủy thủ đề nghị với Straus - nhà sáng lập công ty bách hóa Macy của Mỹ và cũng là người giàu thứ 2 thế giới, \"Tôi đảm bảo sẽ không ai phản đối việc một người đáng kính như Ngài bước lên thuyền cứu hộ đâu\". Straus đáp, \"Tôi sẽ không bỏ đi khi những người đàn ông khác còn ở lại\". Khi ông cố gắng khuyên vợ mình lên thuyền cứu hộ, bà vẫn một mực từ chối, \"Bao nhiêu năm qua, anh đi đâu là em theo đó. Bây giờ, em sẽ cùng anh đi đến bất cứ nơi nào anh muốn\".</p><p>Sau đó, ông choàng tay vợ rồi thong thả bước đến chiếc ghế trên boong tàu, ngồi xuống và đón chờ giây phút cuối cùng của cuộc đời. Ngày nay, tại khu Bronx thuộc thành phố New York, người ta dựng một tượng đài để tưởng niệm vợ chồng Straus. Trên đó khắc dòng chữ, \"Tình yêu không thể nhấn chìm bởi đại dương\".</p><p>Trong giờ phút nguy kịch, Lydepas ôm chặt người chồng mới cưới vì cô không muốn thoát chết một mình. Vì bất đắc dĩ, người chồng phải đấm cô ngất xỉu. Khi tỉnh lại thì cô đã thấy mình nằm trên chiếc thuyền cứu hộ trôi lênh đênh ngoài biển. Về sau, Lydepas cả đời không tái giá, sống độc thân để tưởng nhớ người chồng quá cố.</p><p>Trong buổi họp mặt những người may mắn sống sót tại Lausanne nước Thụy Sĩ, bà Smith bộc bạch. \"Lúc đó hai đứa con của tôi được bế lên thuyền cứu hộ nhưng thuyền đã quá tải nên không thể chở thêm tôi nữa. Khi ấy, một phụ nữ trên thuyền cứu hộ đã đứng dậy nhường chỗ cho tôi và nói, \"Trẻ con không thể sống thiếu mẹ\". Bà hối tiếc vì đã không hỏi tên người phụ nữ đó</p><p>Khi đuôi tàu bắt đầu chìm xuống nước, Charles nghe thấy những lời yêu thương vang lên trong khoảnh khắc cuối cùng của cuộc đời. Trong bức màn đêm đen tối nhuốm đẫm đau khổ và chia ly, tinh thần qúy tộc nổi lên như ngọn đuốc rực sáng và khắc họa nên một tuyệt tác vĩ đại về nhân cách và đạo đức con người. Giáo dục lối sống không đơn giản là lý thuyết, mà là khi đối diện với thực tế, những bài học đạo đức và nhân cách hôm nay vẫn cần được thực hành một cách tuyệt đối.</p><p>Trích \"Câu chuyện đằng sau thảm họa Titanic\" - Chicken soup for the Golden Soul - \"Sức mạnh của sự tử tế\".</p>', '2023-05-12 00:31:07', 4);
INSERT INTO `bai_viet` (`id`, `tieu_de`, `hinh_anh`, `noi_dung`, `ngay`, `nguoi_viet_id`) VALUES
(30, 'Bài học từ chiếc túi hàng hiệu', './public/img/1683852036640-bd6b8880efdf11edb421df1cb164742c.png', '<p>Mình sở hữu chiếc túi designer bag đầu tiên cách đây vài năm, lúc mình mới đi du học. Để mà nói thì túi hàng hiệu là một trong những \'\'life goals\" của mình đặt ra khi còn bé, kiểu như hình ảnh ẩn dụ cho sự thành công của mình ý :D.</p><p>Ấy thế mà, sau khi đã có vài chiếc tuy không nhiều nhưng đến thời điểm bây giờ mình không còn sở hữu một cái nào nữa.</p><h2><strong>1. Chiếc túi sinh ra để phục vụ bạn</strong></h2><p>Cảm giác phấn khích khi đeo túi giống như khi con gái diện một bộ quần áo đẹp, chẳng để cho ai ngắm chỉ là tự nhìn vào gương cảm thấy mình xinh đẹp là vui rồi.</p><p>Nhưng một đứa overthinking và hướng nội như mình mỗi khi đeo túi lại luôn suy nghĩ :\" Nhỡ người ta nghĩ mình giàu và giỏi lắm thì sao? Mình chỉ thích thời trang chứ không muốn bị hiểu nhầm là một đứa khoe mẽ và gây chú ý\".</p><p>Dù thật ra chả ai quan tâm rằng mình đang làm gì và mình là ai ở một đất nước tư bản như thế này. Nhưng điều đó cũng không thể giúp mình ngăn lại cảm giác đó khi dùng.</p><p>Mỗi lần dùng mình lại phải nâng niu giữ gìn cẩn thận vì sợ trầy túi.</p><p>Lâu dần niềm vui của mình không còn nữa, cảm xúc của mình phụ thuộc vào chiếc túi trong khi đáng ra là <strong>nó phục vụ cho mình.</strong></p><h2><strong>2. Bài học về sự tối giản</strong></h2><p>Cuộc sống có quá nhiều điều phải lo toan suy nghĩ, những chiếc túi bắt đầu với những niềm vui thử thách khi chinh phục thành những áp lực về tài chính không cần thiết trong cuộc đời mình. Bài toán benefit and cost lại hiện ra rõ ràng hơn bao giờ hết.</p><p>Thêm nữa, hầu hết những chiếc túi hàng hiệu đều có làm bằng da thật, quai túi chắc chắn và chính điều đó làm nên giá trị cho chúng hơn những loại thông thường khác. Nhưng vì đồ đạc của mình khá nhiều mỗi lần xách những chiếc túi nặng khiến mình cảm thấy mệt mỏi và đau đầu khi phải cố gắng nhồi nhét vào những chiếc túi hay xách thêm đồ vào một túi canvas khác không thoải mái như thế.</p><p>Mình không muốn cả cuộc sống của mình xoay quanh chiếc túi, cả trong suy nghĩ lẫn đời sống hàng ngày.</p><h2><strong>3. Bài học về niềm vui</strong></h2><p>Sau khi không còn sở hữu chiếc túi nào mình vẫn vui vẻ với cuộc sống hiện tại của mình, niềm vui của mình dần trở nên đơn giản hơn ngoài những giá trị ngoại tại. Mình vui vẻ khi dùng những chiếc túi local brand xinh đẹp, không phải quá chú ý khi sử dụng. Mình không ngại chi nhiều tiền cho những khoá học, những chuyến đi khám phá hay những cuốn sách hay và chúng mang lại niềm vui cho mình hơn bao giờ hết.</p><p>Cuộc sống dần trở nên tối giản, và thành công đối với mình không cần được chứng minh qua việc sở hữu túi đẹp như khi còn bé nữa mà nó nằm ở chính bên trong nội tại của mình.</p><p>Mình nhận ra việc mình bị overthinking không phải mình sở hữu thứ đắt tiền mà bởi<strong> bản thân mình vẫn chưa tự tin vào giá trị của chính mình</strong> và đấy là lúc mình nên quay vào bên trong làm việc với chính mình xem điều gì đối với mình là thực sự quan trọng. Cuộc đời mình cần đạt được nhiều giá trị quan trọng hơn là vài cái túi dù điều đó chẳng cần ai ghi nhận miễn mình cảm thấy hài lòng với bản thân là được.</p>', '2023-05-12 00:40:36', 4);
INSERT INTO `bai_viet` (`id`, `tieu_de`, `hinh_anh`, `noi_dung`, `ngay`, `nguoi_viet_id`) VALUES
(32, 'Comfort food: Nem rán', './public/img/1686271900451-5681aef0029b11eea088b74cb46c8e56.jpeg', '<p>Nem rán vẫn luôn là comfort food của mình. Một lựa chọn thật dễ dàng, dễ dàng đến mức có thể bật ra ngay mà không cần suy nghĩ. Vì đơn giản, lựa chọn này đến từ tình cảm thay vì lí trí. Tình cảm của mình dành cho nem rán là thứ tình cảm thật tự nhiên và hiển nhiên, đồng thời cho mình cảm giác vô cùng an toàn và vỗ về, giống như tình cảm dành cho gia đình vậy. Và dù cho thế nào, với mình, một bữa cơm với đĩa nem nóng hổi vẫn luôn là hình ảnh của một nơi trú ẩn yên bình.</p><p>Nghĩ đến nem rán là nghĩ đến sự háo hức xôn xao. Cảm giác ấy bắt đầu khi ai đó thông báo rằng cuối tuần nhà mình làm nem nhé, và thế là sự mong chờ đến cuối tuần cứ thế âm ỉ suốt mấy ngày đi học đi làm. Người lớn tất bật kiểm tra nguyên liệu trong tủ xem thừa thiếu cái gì. Thanh niên dành riêng hẳn một buổi ở nhà, từ chối mọi kèo đi chơi. Trẻ con thì háo hức được phụ đi chợ để được chút quà vặt làm thù lao cho việc xách đồ. Suốt cả tuần, ai cũng như có thêm một lý do, một cột mốc, một đích đến, dù đơn giản, để quên đi mệt nhọc, để làm động lực cố gắng, để hướng tới.</p><p>Nghĩ đến nem rán là nghĩ đến sự quây quần sum vầy. Bản chất món nem có rất nhiều nguyên liệu, ngoài ra còn phải qua rất nhiều bước sơ chế. Vì thế mà mỗi buổi làm nem sẽ huy động cả gia đình xắn tay vào lao động. Mọi người cứ thế mà làm đủ việc: từ rửa rau, thái miến, đến trộn nhân, nhúng bánh, cuốn nem, chiên nem. Các bước làm nem đều đan xen và hoà quyện, vì thế mà buổi làm nem luôn có sự tương tác liên tục giữa mọi thành viên, thành thử ra lúc nào cũng rôm rả nói cười. Nhân nem vô cùng đa dạng, nào thịt thà tôm cua, nào su hào cà rốt, nào giá đỗ củ đậu, nào nấm, nào miến. Mỗi cây mỗi hoa, mỗi nhà mỗi cảnh. Mỗi gia đình sẽ có khẩu vị của riêng họ, và vì thế nguyên liệu làm nhân cũng được gia giảm tuỳ sở thích mỗi nhà. Rồi đập thêm vài quả trứng, biến nhân nem từ một hỗn hợp dễ bị tách rời thành một khối hoà quyện gắn kết, và cuối cùng là được cuốn lại bằng bánh đa nem. Nhìn món nem, có thể thấy được phần nào sự hiện thân của hình ảnh gia đình: tuy đa dạng đa sắc, nhưng lại rất gắn bó và chắc chắn. Chẳng phải tự dưng mà món nem thường xuyên xuất hiện ở những dịp mang tính chất gia đình như lễ tết, tụ họp, cưới xin.</p><p>Nghĩ đến nem rán là nghĩ đến cảm giác được chiều chuộng và vỗ về. Vốn là món ăn vừa nhiều đạm lại còn mỡ màng, nhiều khi lại còn nhiều tinh bột khi thường được ăn kèm với bún hoặc cơm, nem rán chẳng khác gì một quả bom calo giữa thời đại thái bình thịnh trị của ăn kiêng và rau củ. Dẫu cho xung quanh là những lời chê bai tinh bột, phản đối dầu mỡ và đả kích thịt thà, một đĩa nem rán vàng ruộm thơm phức là đủ để tạm bỏ ngoài tai những lời khuyên dinh dưỡng và những tiêu chuẩn hình thể. Chẳng khác nào một sự chiều chuộng dịu dàng, đôi khi là chiều hư, cho những đứa nhóc muốn tạm chạy trốn khỏi thế giới hà khắc đầy định kiến này.</p><p>Nghĩ đến nem rán là nghĩ đến niềm an ủi cho người xa xứ. Cứ tết đến xuân về, nhìn cảnh mấy đứa du học sinh đón tết với nhau, mâm cỗ nào cũng thấy đĩa nem rán. Quả là đã thành luật bất thành văn: đã là tết thì phải có nem. Cũng từng ấy những bước chuẩn bị, cũng rôm rả xôn xao hỉ hả nói cười, cũng nhịp nhàng hợp tác để có được thành phẩm cuối cùng. Tính ra cũng là làm nem với gia đình, chỉ là với một kiểu gia đình khác: một gia đình hình thành từ những con người chẳng cùng máu mủ ruột rà, nhưng lại chung nhau nỗi nhớ nhà da diết. Ngửi mùi nem rán thơm mãnh liệt toả ra từ chảo dầu sóng sánh, cắn miếng nem giòn rụm bốc khói nghi ngút, chan bát canh măng, nhai lát bánh chưng, bỗng thấy xứ người cách xa ngàn cây số giờ cũng gần như là nhà.</p><p>Mặt khác, đôi lúc, nghĩ đến nem rán cũng nhận ra được vài điều liên quan đến tình thương dịu dàng của lứa đôi. Trước xem Đu Đồ Đút nói về việc tại sao nam giới nên biết nấu ăn, có đoạn:</p><blockquote><p><i>“Các cô gái, hãy yêu một chàng trai biết nấu ăn. Yêu chàng trai biết nấu ăn, vì người ta sẽ quan tâm đến em như cái cách người ta làm nem. Đủ chắc, để em không thể tách rời. Nhưng không quá mức, làm em phát bực vì nóng”</i></p></blockquote><p>Yêu chàng trai biết nấu ăn, vì tâm trí người ta dành cho em như cách người ta nghĩ đến việc làm nem. Toàn tâm toàn ý vì em. Chuẩn bị kỹ càng mọi thứ cho em. Và khi xa em, người ta sẽ nhớ đến em với sự háo hức xôn xao. Em sẽ là nguồn động lực, tuy giản đơn nhưng không hề nhỏ bé, để người ta hướng về.&nbsp;</p><p>Yêu chàng trai biết nấu ăn, vì tình cảm người ta dành cho em như là chiếc nem người ta làm. Thô ráp bên ngoài, nhưng lại mềm mại ấm nóng bên trong. Sẽ cho em cảm giác được chiều chuộng vỗ về. Sẽ cho em một nơi chốn để em có thể gọi là nhà. Vì đó là nem. Vì đó là em.</p><p>Mình có một phát hiện nho nhỏ nhờ món ăn này. Lúc còn ở gần gia đình thì vụng về lóng ngóng, chỉ xử được những phần lặt vặt khi làm nem. Nhưng từ khi đi học đi làm xa nhà, bỗng dưng lại biết làm từ đầu đến cuối. Như một cột mốc trưởng thành. Như một bản năng. Như một phản xạ vô điều kiện. Cũng giống như tình cảm mình dành cho nem vậy: vô điều kiện.</p>', '2023-06-09 00:51:40', 6),
(40, 'Chúng ta có phải là một thế hệ “chán đi làm”?', './public/img/1686707265042-3dc68c1004e811ee989a3955e3b71b5e.jpeg', '<p>Mình gặp M, một sinh viên mới tốt nghiệp vào một ngày hè oi ả. Thằng bé nói, dạo này chán quá chị ạ vì em đã nghỉ việc chỉ sau 2 tháng đi làm. Vì sao ấy ạ, thì em không biết mình làm được gì ở đấy nữa, không biết tương lai sẽ đi về đâu.</p><p>T, một người đồng nghiệp cũ xuất sắc của mình, chuyển tới công ty thứ 3 trong vòng 3 năm. T là người nhanh nhạy và có chuyên môn rất tốt, nhưng liên tục nhảy việc vì luôn cảm thấy có gì đó “chưa đủ” ở sếp, đồng nghiệp, con đường thăng tiến…</p><p>Thực tế là, ngay cả khi còn là lính mới tò te, hay đã ở vai trò đồng sáng lập của một startup như hiện nay, luôn có những lúc mình cảm thấy chán làm việc vcđ (xin lỗi nhưng cảm giác chính xác là vậy đó). Có những ngày thực sự rất là NẢN trong công việc vì một tỷ thứ áp lực vô hình. Những ai đang đọc bài này dù là nhân viên hay quản lý, hãy nói thật cho mình biết bạn có từng cảm thấy vậy không?</p><p>Nếu bạn biết tới những khái niệm “Blue Monday” (Nỗi buồn thứ 2) và “TGIF” (Ơn giời thứ 6 đây rồi) thì bạn không cá biệt đâu, vì thực ra có hẳn một thế hệ người tự định nghĩa mình là “nằm thẳng”: họ quá mệt mỏi với việc phải cố gắng trong môi trường làm việc ngày càng cạnh tranh khốc liệt, thậm chí từ chối gia nhập vào lực lượng lao động. Một khảo sát của Deloitte Global cho biết, 40% Gen Z và 24% Gen Y muốn nhảy việc trong giai đoạn 2021-2022.</p><figure class=\"image\"><img src=\"http://localhost:8080/./public/img/1686714221692-3dc68c1004e811ee989a3955e3b71b5e.jpeg\"><figcaption>Lying flat generation - Thế hệ \"nằm thẳng\"</figcaption></figure><blockquote><p><i><strong>Cách duy nhất để giải quyết triệt để vấn đề, là đối diện với nó</strong></i></p><p>Lời ai đó nói</p></blockquote><p>Trong suốt gần mười năm đi làm trong các môi trường khác nhau, mình dần dần nhận ra rằng mỗi người chúng ta không cần một điểm “cân bằng” hoàn hảo, có lẽ ta chỉ cần tìm ra điểm “phù hợp”. Và điều quan trọng nhất thực ra là học cách để tìm thấy sự thỏa mãn với chính công việc mình đang có - bất kể bạn có một người sếp chưa đủ tâm lý, một môi trường làm việc không quá cởi mở, hay một cơ chế đãi ngộ chưa thật hấp dẫn. Cần <i><strong>\"Học cách Yêu-công-việc-mình-làm\"</strong></i>.</p><p>Mình rất thích một khái niệm được Stefan Falk đưa ra trong cuốn <strong>Intrinsic Motivation</strong> là \"<i><strong>FEO (Focus on Exciting Outcome)</strong></i>\", tạm dịch là “tập trung vào những thứ làm bạn cảm thấy hứng khởi”. Hôm nay có điều gì thực sự khiến bạn rất vui khi hoàn thành? Bạn sẽ hứng khởi kể lại cho người yêu/vợ chồng/con cái/bố mẹ của mình điều gì đáng nhớ nhất ở chỗ làm sau khi kết thúc một ngày dài? Tuần này có mục tiêu nào khiến bạn hào hứng? Và tháng này? Và năm nay?</p><figure class=\"image\"><img src=\"http://localhost:8080/./public/img/1686714281891-997c58c004e111ee989a3955e3b71b5e.jpeg\"><figcaption>Tình yêu công việc bền vững</figcaption></figure><p>Trong vòng 4 tháng nghiên cứu và tìm hiểu về cuốn sách này, mình và các đồng nghiệp ở Spiderum đã thực hành nhiều công cụ được Stefan Falk đưa ra trong cuốn <a href=\"https://shope.ee/Az1bUywfB\"><strong>Intrinsic Motivation</strong></a> nhằm giúp các cá nhân tìm thấy niềm vui bền vững trong công việc thay vì tưởng thưởng bề ngoài. Tất nhiên bọn mình không phải những người đầu tiên, những công cụ này đã được Stefan sử dụng để huấn luyện cho khoảng 4.000 nhà lãnh đạo tại 60 tổ chức khác nhau ở Bắc Mỹ và châu Âu để giúp họ nâng cao hiệu suất công việc. Có thể liệt kê một vài ví dụ bọn mình đã trực tiếp áp dụng và thấy hiệu quả rõ rệt, như là:</p><p>- <strong>Daily Journaling (viết Nhật ký ngày)</strong>: Đối thoại với chính mình để trả lời 3 câu hỏi: Hôm nay mình cảm thấy vui nhất về điều gì? Có việc gì khó khăn &amp; cách giải quyết ra sao? Mình biết ơn ai/cái gì trong ngày hôm nay?</p><p>Trong giai đoạn căng thẳng cực độ, việc thực hành viết Nhật ký ngày đã giúp cho mình vượt qua những cảm xúc tiêu cực nhất cũng như có niềm tin vào bản thân hơn rất nhiều. Nó cũng giúp cho tâm trí được bình ổn và cảm xúc được kiểm soát tốt hơn, tránh những việc “buồn quá mức” hay “vui quá đà”. Việc viết giúp mình nhận ra: hóa ra mình đã làm được nhiều việc tốt hơn mình tưởng, thay vì chỉ tập trung vào những sự kiện tiêu cực.</p><p>Mình cực kỳ ấn tượng với những gì việc thực hành viết Daily Journal đã tác động tới bản thân mình, và vì thế, chắc chắn mình sẽ viết riêng một bài để nói sâu về Daily Journal. Mong các bạn ủng hộ ^^!</p><p>- <strong>Đừng là nạn nhân, hãy là thám tử</strong>: Đây là việc thay đổi mindset đổ lỗi bằng việc đứng ra ngoài từ góc độ khách quan để phân tích, bóc tách sự việc. Vấn đề lớn nhất của thất bại không phải là bản thân của sự thất bại, mà là nỗi đau tinh thần nó gây ra. Điều tương tự cũng xảy ra khi chúng ta trải nghiệm thành công - cảm xúc vui mừng có thể dễ dàng khiến chúng ta đánh giá quá cao bản thân.</p><p>- <strong>Đưa mọi thứ cần làm lên lịch (Calendar view)</strong> thay vì Checklist: Việc đưa các task lên lịch sẽ giúp cho bạn quản lý tốt không chỉ về mặt số lượng, mà còn là mức độ ưu tiên, khoảng thời gian cần thiết để chuẩn bị, và nhìn rõ sự liên kết giữa các task. Đây là một công cụ cực kỳ đơn giản mà hiệu quả, been there done that nhé!&nbsp;</p><p>- <strong>Áp dụng tư duy logic trong những trường hợp bạn không chắc chắn</strong>:</p><p><i>Đều mà tôi hoàn toàn chắc chắn mình nên làm?</i></p><p><i>Điều mà tôi nghĩ mình nên làm nhưng chưa chắc chắn? Tôi có thể hỏi ai?</i></p><p><i>Trong số những điều nên làm, tôi mù tịt về cái gì? Tôi có thể hỏi ai?</i></p><p>Trong quá trình xuất bản 50,000 cuốn sách hướng nghiệp Người Trong Muôn Nghề, mình và đội ngũ Spiderum nhận ra rằng việc chọn một công việc để làm là chưa đủ, mà việc học cách thích nghi, yêu lấy công việc của mình, nỗ lực tiến lên thay vì đổ lỗi cho hoàn cảnh… là một kỹ năng cần có của thế hệ Gen Z, Gen Y ngày nay.&nbsp;</p><p>Và thế là sau khoảng thời gian dài nghiên cứu, bọn mình quyết định đem Intrinsic Motivation của Stefan Falk về Việt Nam với tên gọi <a href=\"https://shope.ee/Az1bUywfB\"><strong>“Động lực nội tại”</strong></a>. Thành thật mà nói, cuốn sách vẫn còn một vài hạt sạn trong khâu biên tập bản gốc mà nếu có cơ hội thì chắc chắn chúng mình sẽ góp ý để tác giả làm tốt hơn ^^ Thế nhưng mình tin cuốn sách là điểm khởi đầu phù hợp với những khái niệm &amp; công cụ được phân loại từ dễ đến khó để bạn có thể vận dụng và thay đổi cách não bộ vận hành. Mình mong cuốn sách sẽ là một món quà ý nghĩa của Spiderum dành tặng cho giới trẻ Việt Nam, sau tủ sách hướng nghiệp Người Trong Muôn Nghề.</p><p>Bởi vì đến cuối cùng, chúng ta không thể cứ mãi chán công việc chỉ vì không hợp sếp hoặc không thích đồng nghiệp.</p><p>Chúng ta làm việc vì những mục tiêu lớn hơn thế chứ? Và vì chính bản thân mình chứ?</p><p>Bạn có thể quyết định vẫn đi làm như một chú zombie lờ đờ mỗi ngày, hoặc có thể đặt sách ở đây ngày hôm nay để có thêm một vài góc nhìn tham khảo nhé: <a href=\"https://shope.ee/Az1bUywfB\"><strong>https://shope.ee/Az1bUywfB</strong></a></p><p>Chúc bạn làm việc vui!</p><p>(Mình đã xin riêng một mã giảm giá dành cho các bạn độc giả Spiderum này <strong>SPIDOLFAN</strong>).</p>', '2023-06-14 01:47:44', 2),
(41, 'Ông Bụt của tôi hiện lên và nói: \"Con à, không ai cứu mày đâu\"', './public/img/1686790244080-49f48b7008ff11ee8413b73dbdd205c6.jpeg', '<p>Bạn có bao giờ tính nhẩm từ khoảnh khắc nào thì bạn bắt đầu ước rằng có một ông bụt hiện ra cứu bạn? Rằng chỉ cần thành tâm cầu nguyện, chỉ cần rơi hai hàng nước mắt từ trái tim thì có một thế lực hiền từ, mạnh mẽ toàn năng sẽ hiện ra hoàn thành, hoặc chí ít là xoa dịu cơn đau? Với tôi thì nó có lẽ bắt đầu từ lâu lắm, từ lúc con mèo đầu tiên của tôi bị xe đâm.</p><p>Sau đó ông Bụt và ông trời lúc nào cũng là câu đầu tiên nhảy trong đầu tôi. Tôi ước mèo của tôi sống lại, ước mình không trượt chuyên, ước mình có người đó để yêu, ước mình không béo, ước mình không trầm cảm, ước mình xinh hơn, ước mình sẽ có nhà thật to, ước mẹ không già đi, ước chân của mẹ đỡ đau và trán của mẹ không nhăn đến thế. Ước sếp tốt hơn, ước đồng nghiệp toxic kia ai cũng ghét. Những điều ước sẽ tăng lên theo cấp số nhân qua từng ngày, thậm chí số mong muốn đó còn nhiều và phức tạp hơn tôi còn nhỏ. Tôi nhận ra tôi lúc nào cũng mang theo một tư tưởng \"cổ tích\", tư tưởng \"ông Bụt\" - rằng em bé trong lòng tôi chưa bao giờ cảm thấy mình lớn cả, lúc nào nó cũng nghĩ rằng cần có một <i>người lớn</i>, lớn hơn cả nó nữa, nhìn nó vô cùng trìu mến và tất nhiên sẽ thực hiện bất cứ yêu cầu nào của nó đưa ra.</p><p>Thật đó, con người thi thoảng hay rơi vào những tình huống mà sự tồn tại của người như ông Bụt là cấp bách lắm. Như hỏng xe giữa đường ngập mưa thì sao? Như sắp sửa uống thuốc kết liễu mình thì sao? Như người mình coi là soulmate thực ra là đồ khốn? Như công việc? Như buổi tối về nhà toàn thân vỡ vụn? Thật đó, xem ra khóc như anh nông dân cây tre trăm đốt cũng không yếu đuối lắm. Vì bạn đâu biết những điều gì có thể quật ngã một con người.</p><figure class=\"image\"><img src=\"http://localhost:8080/./public/img/1686714082191-213e558008ff11ee8413b73dbdd205c6.jpeg\"></figure><p>Trong tâm lý học của Jung, hình mẫu ông Bụt có thể được xếp cùng loại với <strong>senex</strong> - một mẫu tượng người đàn ông lớn tuổi, râu tóc bạc phơ, vô cùng uyên bác đóng vai trò dẫn dắt trên hành trình của người anh hùng. Đây là mẫu tượng khá phổ biến, không chỉ trong văn hóa (ông bụt, ông tiên), trong phim ảnh (phù thủy, Thái thượng lão quân gì đó, Gandalf...) mà còn trong cả tarot (có thể là The hermit) và những sản phẩm truyền thông mà con người tạo ra một cách vô thức. Đúng thế - con người luôn mong muốn được ngồi khóc huhu chờ ông Bụt của họ tới một cách kỳ diệu, không thể tin được.</p><p>Ôi, nhưng ông Bụt của tôi thì không hề như thế. Ông Bụt của tất cả chúng ta đều seen tin nhắn, hoặc nếu họa hoằn lắm trả lời thì cũng chỉ ném cho một câu: \"Không ai cứu mày đâu.\" Thế mới bực chứ. Vậy là sếp của tôi lúc đó sẽ không bao giờ ngẩng đầu, mở mắt lên để ghi nhận những tháng ngày khổ sai kinh điển, không ai đòi lại công bằng cho tôi để quay ra ghét bà đồng nghiệp toxic, cho dù bà ấy có toxic cỡ nào hay cho dù bà ấy có tồn tại. Không ai sang bảo hàng xóm cho loa bé đi giùm - bởi ngoài lý do senex ra thì Việt Nam cũng đồng thời là một nước đi theo văn hóa High uncertainty avoidance - Văn hóa Tránh sự không chắc chắn. Không ai tát vô mặt người yêu cũ của tôi 100 cái. Không ai bảo anh kia đừng có chen vào hàng đổ xăng của tôi. Ôi ôi.</p><figure class=\"image\"><img src=\"http://localhost:8080/./public/img/1686714105394-8c87dc90090311eeb8ccebe33f04e2bf.png\"></figure><p>Điều ấy, sau tất cả, cũng không tồi tệ gì cho lắm. Nhưng chỉ khi bạn nhận ra có một em bé ở trong bạn lúc nào cũng cần mẫn cố gắng để được lắng nghe thôi ấy. Chỉ khi bạn biết rằng mình đã lớn, mình đã không còn là một đứa bé sơ sinh da mỏng dính, không thể tự làm mọi thứ; mình đã có thể nâng một bao gạo hàng chục cân, mình đã có thể tự đi xem phim, đã có thể mua món ăn mình thích. Có lẽ em bé ấy sẽ thấy an tâm hơn: vì giờ đây nó đã có thể tự làm ông Bụt của mình, tự đáp ứng những nhu cầu dù nhỏ nhặt hay to tát đến mấy, tự mang tới cho mình được niềm vui, sự bình yên sau một cơn đau. Em bé - chúng ta - tôi - hay ai - sẽ luôn tồn tại, nhưng điều quan trọng nhất là cách bọn mình định nghĩa.</p>', '2023-06-14 01:48:50', 2);

